import { useState } from 'react'

const ListRender = () => {

    const [list] = useState(["Gustavo", "Bruno", "Amanda", "Catarina", "Astofo", "Praia"])

    const [users,setUsers] = useState([{id:1,name:'Gustavo',age:2},{id:3,name:'Astofo',age:34},{id:4,name:'Olivaniar',age:3}])

   const deleteRandom = () => {

    const randomNumber = Math.floor(Math.random() * 5)

    setUsers(users.filter(user => (user.id ==! randomNumber)))


   }


    return (
        <div>
            <div>
                <h3> List Rendering</h3>
                {list.map((item) => (
                    <li key={item.id}>{item}</li>))}
            </div>
            <div>
                {users.map((item)=> (
                    <li key={item.id}>{item.name +' '+ item.age}</li>
                ))}
            </div>

            <div>
                <button onClick={deleteRandom}> Delete random</button>
            </div>
        </div>
    )

}

export default ListRender;
